import React from 'react';
import { Modal, TextField, Button, InputLabel, Input } from '@mui/material';
import { StyledModal } from '../appStyle';
// import chart from '../pic';

// Function to add text to slides
function AddElementModal ({ open, modalCloseFunction, slideId, pageId, slides, setSlidesFunction, updateSlidesFunction, elementType }) {
  const [element, setElement] = React.useState('');
  const [size, setSize] = React.useState(10);
  const [text, setText] = React.useState('');
  const [fontSize, setFontSize] = React.useState(5);
  const [colour, setColour] = React.useState('#000000');
  const [imageFile, setImageFile] = React.useState('');
  const [imageUrl, setImageUrl] = React.useState('');
  const [alt, setAlt] = React.useState('');

  // The state are updated after the rendering (prevent infinite loop )
  React.useEffect(() => {
    console.log('New added element:', element);
    if (element) {
      const newSlidesArray = [...slides];
      const slideIndex = newSlidesArray.findIndex(slide => slide.url.includes(pageId));
      if (slideIndex !== -1) {
        const slide = newSlidesArray[slideIndex];
        const slideElements = slide.slideElements || [];
        const newSlide = {
          ...slide,
          slideElements: [...slideElements, element]
        }
        newSlidesArray[slideIndex] = newSlide;
        setSlidesFunction(newSlidesArray);
        updateSlidesFunction(slideId, newSlidesArray);
      }
    }
  }, [element])

  // Function to upload image from the local source
  // Read the content of the file and convert into base64 string
  const uploadImage = (event) => {
    const file = event.target.files[0];
    console.log(file);
    // Read the file
    const reader = new FileReader();
    reader.readAsBinaryString(file);
    reader.onload = (event) => {
      // Extract base64 encoded string when finish reading
      const b64String = btoa(event.target.result);
      setImageFile(b64String);
      console.log(imageFile);
    };
  };

  const addText = async () => {
    // Close the modal
    modalCloseFunction();

    // New added text object
    const newText = {
      element: 'text',
      slideId: slideId,
      pageId: pageId,
      size: size,
      text: text,
      fontSize: fontSize,
      colour: colour,
      // Always at the top left position
      position: 'relative',
      top: '0',
      left: '0',
      // Left-aligned
      textAlign: 'left',
      // Soft grey border
      border: '1px solid #cccccc',
      // Cut overflowing content
      overflow: 'hidden',
      textOverflow: '-',
      whiteSpace: 'nowrap',
    };
    console.log('New added text:', newText);
    setElement(newText);
  }

  const addImage = async () => {
    // Close the modal
    modalCloseFunction();

    // Check if either file is uploaded
    if ((imageFile === '') & (imageUrl === '')) {
      alert('Please upload an image file or input the URL');
    }
    // If there's file uploaded, the image url will be the base64 encoded url
    // Otherwise, the image url will be the input url
    const image = imageFile ? `data:image/jpeg;base64,${imageFile}` : imageUrl;
    console.log('Image src:', image);

    // New added text object
    const newImage = {
      element: 'image',
      slideId: slideId,
      pageId: pageId,
      size: size,
      image: image,
      alt: alt,
      // Always at the top left position
      position: 'relative',
      top: '0',
      left: '0',
    };
    console.log('New added image:', newImage);
    setElement(newImage);
  }
  return (
    <Modal
      open={open}
      onClose={modalCloseFunction}
      aria-labelledby="child-modal-title"
      aria-describedby="child-modal-description"
    >
      <StyledModal>
        <h2 id="child-modal-title">{elementType === 'text' ? 'Add Text' : 'Add Image'}</h2>
        {(elementType === 'text') && (
          <>
            <TextField label="Size (%) " onChange={event => setSize(event.target.value)} required variant="outlined" type="number" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth value={size} />
            <TextField label="Text" onChange={event => setText(event.target.value)} required variant="outlined" type="text" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth value={text} />
            <TextField label="Font Size (em)" onChange={event => setFontSize(event.target.value)} required variant="outlined" type="number" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth value={fontSize} />
            <TextField label="Colour" onChange={event => setColour(event.target.value)} required variant="outlined" type="color" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth value={colour} /><Button variant="contained" size="small" onClick={addText} type="submit">Add Text</Button>
          </>
        )}
        {(elementType === 'image') && (
          <>
            <TextField label="Size (%) " onChange={event => setSize(event.target.value)} required variant="outlined" type="number" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth value={size} />
            <TextField label="Image URL" onChange={event => setImageUrl(event.target.value)} variant="outlined" type="text" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth value={imageUrl} />
            <InputLabel htmlFor="upload-image">Upload Image</InputLabel>
            <Input onChange={event => uploadImage(event)} accept="jpeg, jpg, png" variant="outlined" type="file" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth />
            <TextField label="Image description " onChange={event => setAlt(event.target.value)} required variant="outlined" type="text" sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth value={alt} />
            <Button variant="contained" size="small" onClick={addImage} type="submit">Add Image</Button>
          </>
        )}
      </StyledModal>
    </Modal>
  );
}

export default AddElementModal;
