import { React } from 'react';
import { useLocation } from 'react-router-dom';
import { StyledSlide } from '../appStyle.js';
import CreateSlides from '../components/CreateSlides';
import RenderText from '../components/RenderText'

function Slide ({ cards }) {
  const path = useLocation().pathname;
  const parts = path.split('/');
  const currentSlideId = parts[1];
  const currentPageId = parts[2];

  // // Measure the deck width and height after it is rendered
  // const deckRef = useRef(null);
  // const [deckWidth, setDeckWidth] = useState(0);
  // useEffect(() => {
  //   if (deckRef.current) {
  //     const width = deckRef.current.offsetWidth;
  //     setDeckWidth(width);
  //     console.log('Deck width:', deckWidth);
  //   }
  // }, []);
  // const [deckHeight, setDeckHeight] = useState(0);
  // useEffect(() => {
  //   if (deckRef.current) {
  //     const height = deckRef.current.offsetWidth;
  //     setDeckHeight(height);
  //     console.log('Deck height:', deckHeight);
  //   }
  // }, []);

  return (
    <StyledSlide>
      <CreateSlides cards={cards} />
      {cards.map((card, cardIndex) => (
        <div key={cardIndex}>
          {card.slides.map((page, pageIndex) => {
            if (card.url.includes(currentSlideId) && pageIndex === currentPageId - 1) {
              // console.log(card.url, currentSlideId);
              // console.log(pageIndex, currentPageId);
              return (
                  <RenderText key={`${cardIndex}-${pageIndex}`} textElements={page.slideElements} />
              );
            } else {
              // Skip rendering if it's not the current page
              return null;
            }
          })}
        </div>
      ))}
    </StyledSlide>
  );
}

export default Slide;
