import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { Link } from 'react-router-dom';

function MediaCard ({ projectName, image, projectContent, projectPage, url }) {
  const cardImage = image || 'gray.jpg';
  return (
    <Link to={url} style={{ textDecoration: 'none' }}>
      <Card sx={{ display: 'flex', minWidth: '100px', maxWidth: '300px', aspectRatio: '2/1' }}>
        <Grid container>
            <Grid item xs={6.5}> {/* 2/3 space for image */}
                <CardMedia
                sx={{ width: '100%', height: '100%', backgroundColor: image ? 'transparent' : '#bdbdbd' }}
                image={cardImage}
                title={projectName || 'thumbnail'}
                />
            </Grid>
            <Grid item xs={5.5} sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-between', minHeight: '100%' }}> {/* 1/3 space for content */}
                <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom style={{ fontSize: '0.8rem', fontWeight: 'bold' }} component="span">
                        {projectName}
                    </Typography>
                    <Typography gutterBottom style={{ fontSize: '0.8rem' }} component="p">
                        {projectContent}
                    </Typography>
                    <Typography gutterBottom style={{ fontSize: '0.7rem' }} component="p">
                        pages: {projectPage}
                    </Typography>
                </CardContent>
            </Grid>
        </Grid>
      </Card>
    </Link>
  );
}
export default MediaCard;
