// Constants
export const URL = 'http://localhost:5005';

// Function to check if both strings given are matched
export const checkStringMatched = (s1, s2) => {
  if (s1 === s2) {
    return true;
  } else {
    return false;
  }
}

// Function to check if all required inputs are given
export const checkRequiredInputs = (...inputs) => {
  let inValidInput = null;
  inputs.forEach((input) => {
    if (input.value.length === 0) {
      console.log(input);
      inValidInput = input.field;
    }
  });
  return inValidInput;
};
