import React from 'react';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
// edit information of card
function EditModal ({ open, handleClose, projectName, setProjectName, handleSubmit, projectContent, setProjectContent }) {
  const handleNameChange = (event) => {
    setProjectName(event.target.value);
  };
  const handleContentChange = (event) => {
    setProjectContent(event.target.value);
  };
  // 处理提交
  const handleSave = () => {
    handleSubmit(projectName);
    handleClose();
  };
  return (
    <Modal open={open} onClose={handleClose}>
      <Box sx={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: 400, bgcolor: 'background.paper', boxShadow: 24, p: 4 }}>
        <TextField
          fullWidth
          label="Project Name"
          variant="outlined"
          value={projectName}
          onChange={handleNameChange}
        />
        <TextField
          fullWidth
          label="Project Content"
          variant="outlined"
          value={projectContent}
          onChange={handleContentChange}
          style={{ marginTop: '1vh' }}
        />
        <Button sx={{ mt: 2 }} fullWidth variant="contained" color="primary" onClick={handleSave}>
          Save Changes
        </Button>
      </Box>
    </Modal>
  );
}
export default EditModal;
