import React, { useEffect, useState } from 'react';
// import React from 'react';
import { Box, Button, ButtonGroup, IconButton, List } from '@mui/material';
import { Link, useParams, Outlet } from 'react-router-dom';
import DeletePresentation from './DeletePresentation';
import BorderColorIcon from '@mui/icons-material/BorderColor';
import EditModal from './EditModal';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import NewToolBar from './NewToolBar';
import DeleteIcon from '@mui/icons-material/Delete';

function CreatePage ({ updatePageNum, updateSlides, findCardById, deleteCard, updateProjectName, updateContent, token }) {
  const [open, setOpen] = React.useState(false);
  const modalOpen = () => {
    setOpen(true);
  };
  const { id } = useParams();
  const card = findCardById(id);
  const projectName = card ? card.projectName : 'Default Project Name';
  const [editOpen, setEditOpen] = React.useState(false); // Controls whether the edit modal box is displayed 控制编辑模态框是否显示
  const [editName, setEditName] = React.useState(''); // Stores the projectName in the input box 存储输入框中的 projectName
  const [editContent, setEditContent] = React.useState(''); // Stores the projectContent in the input box 存储输入框中的 projectContent
  const [slides, setSlides] = useState(card ? card.slides : []); // Storing a single slide 存储单个slide
  const handleEditOpen = () => {
    setEditName(projectName);
    setEditOpen(true);
  };
  // Handling the operation of submitting edits 处理提交编辑的操作
  const handleEditSubmit = () => {
    updateProjectName(id, editName);
    updateContent(id, editContent);
    setEditOpen(false);
  };
  const encodedName = btoa(projectName);
  const [slideAdded, setSlideAdded] = useState(false);
  const [slideCounter, setSlideCounter] = useState(2);
  const addSlide = () => {
    // Initialize the newSlideCounter with the default slideCounter value
    let newSlideCounter = slideCounter;
    // Check if there are any slides and if the last slide's URL ends with a number
    if (slides.length) {
      const lastSlideUrl = slides[slides.length - 1].url;
      const urlParts = lastSlideUrl.split('/');
      const lastPart = urlParts[urlParts.length - 1];
      // console.log('lastPart:', lastPart);
      // If the last part is a number and the slideCounter is at its initial value
      if (slideCounter === 2) {
        newSlideCounter = parseInt(lastPart, 10) + slideCounter - 1;
        console.log('newSlideCounter', newSlideCounter);
      }
    }
    // Use newSlideCounter to construct the new slide's URL
    const newSlide = {
      url: `/${encodedName}/${newSlideCounter}`,
      slideElements: []
    };
    const newSlidesArray = [...slides, newSlide];
    setSlides(newSlidesArray);
    console.log('Slides array after adding a slide:', newSlidesArray);
    setSlides(newSlidesArray);
    updatePageNum(id, newSlidesArray.length);
    setSlideAdded(true);
    if (slides.length) {
      const lastSlideUrl = slides[slides.length - 1].url;
      const urlParts = lastSlideUrl.split('/');
      const lastPart = urlParts[urlParts.length - 1];
      // console.log('lastPart:', lastPart);
      // If the last part is a number and the slideCounter is at its initial value
      if (slideCounter === 2) {
        setSlideCounter(slideCounter + parseInt(lastPart, 10));
        console.log('counter=', slideCounter);
      } else {
        setSlideCounter(slideCounter + 1);
      }
    }
  };
  useEffect(() => {
    // When the component is loaded, this function is called 当组件加载完成后，这个函数会被调用
    const initializeSlide = () => {
      const newSlide = {
        // Create a new slide object, adding other properties as needed. 创建新slide对象，根据需要添加其他属性
        url: `/${encodedName}/1`, // Set the URL of the new slide here 这里设置新slide的URL
        slideElements: [],
      };
      setSlides(prevSlides => [...prevSlides, newSlide]); // Add a new slide to the array 添加新slide到数组
    };
    // 调用初始化函数添加slide
    if (!slides.length) {
      initializeSlide();
    }
    console.log('初始化后的card数组:', card);
  }, []); // An empty array as a dependency means that the effect will only be run once when the component is first mounted. 空数组作为依赖项意味着这个effect只会在组件首次挂载时运行一次
  useEffect(() => {
    // When slides are updated and slideAdded is true, perform the update operation 当slides更新且slideAdded为true时，执行更新操作
    if (slideAdded) {
      updateSlides(id, slides);
      setSlideAdded(false); // Reset slideAdded state 重置slideAdded状态
    }
  }, [slides, slideAdded, id, updateSlides]);
  const deleteSlide = (slideIndex) => {
    const newSlidesArray = slides.filter((_, index) => index !== slideIndex);
    setSlides(newSlidesArray);
    updateSlides(id, newSlidesArray);
    updatePageNum(id, newSlidesArray.length);
  };

  return (
    <>
      <Box sx={{ display: 'flex', flexGrow: '1', height: '100vh', position: 'absolute', width: '100vw' }}>
        {/* tool bar */}
        {/* <Box sx={{ width: '7vw', bgcolor: '#222225', color: 'white', height: '90vh', paddingTop: '10vh' }}></Box> */}
        <NewToolBar token={token} slides={slides} setSlidesFunction={setSlides} updateSlidesFunction={updateSlides} />
         {/* Nav bar */}
        <Box sx={{ width: '11vw', bgcolor: '#2F2F33', color: 'white', paddingX: '1vw', height: '90vh', paddingTop: '10vh' }}>
          <Button onClick={addSlide} variant="contained" endIcon={<AddCircleIcon />} sx={{ width: '100%', mt: '2vh', display: { xs: 'none', md: 'block' } }}>
            New Slide
          </Button>
          <IconButton onClick={addSlide} color="primary" size="small" sx={{ mt: '2vh', display: { xs: 'block', md: 'none' } }}>
            <AddCircleIcon />
          </IconButton>
          <List sx={{ marginTop: '10px' }}>
            {slides.map((slide, index) => (
              <li key={index} style={{ display: 'inline-block' }}>
                <Link to={`${slide.url}`} style={{ color: 'white' }}>Page {index + 1}</Link>
                <IconButton onClick={() => deleteSlide(index)} aria-label="delete" sx={{ color: 'white', height: '5vh', width: '5vh', marginLeft: '-6%', marginTop: '-8%' }}>
                  <DeleteIcon sx={{ fontSize: 20 }} color='error' />
                </IconButton>
              </li>
            ))}
          </List>
        </Box>
        <Box sx={{ bgcolor: '#222225', color: 'white', flexGrow: 1, paddingTop: '10vh', height: '90vh' }}>
          <ButtonGroup variant="text" aria-label="Basic button group">
            <Link to='/'>
                <Button>Back</Button>
            </Link>
            <Button onClick={modalOpen}>Delete Presentation</Button>
          </ButtonGroup>
          <div style={{ paddingLeft: '10px', display: 'flex' }}>
            <h3>{projectName}</h3>
            <IconButton onClick={handleEditOpen} aria-label="delete" style={{ color: 'white', marginLeft: '0.5vw', height: '4vh', width: '4vh', marginTop: '2vh' }} size='small'>
              <BorderColorIcon fontSize="inherit" />
            </IconButton>
            <EditModal open={editOpen} handleClose={() => setEditOpen(false)} projectName={editName} setProjectName={setEditName} projectContent={editContent} setProjectContent={setEditContent} handleSubmit={handleEditSubmit}/>
          </div>
          <Outlet />
        </Box>
        <DeletePresentation open={open} setOpen={setOpen} deleteCard={deleteCard} id={id} />
      </Box>
    </>
  );
}

export default CreatePage;
