// Imports
import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

import LogoutButton from '../components/LogoutButton';

// Header for login/register page
function Header ({ token, setTokenFunction }) {
  // Get the path name to assign the text of the button
  const path = useLocation().pathname;
  const navigate = useNavigate();

  // Navigate to login or register page
  const loginRegister = async () => {
    if (path === '/login') {
      navigate('/register');
    } else {
      navigate('/login');
    }
  }
  return (
    <Box sx={{ flexGrow: 1, zIndex: '2' }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Presto
          </Typography>
          {/* Conditionally render the button and change its text */}
          {(path === '/login' || path === '/register') && (
            <Button onClick={loginRegister} variant="outlined" color="inherit">
              {path === '/login' ? 'Register' : 'Login'}
            </Button>
          )}
          {!(path === '/login' || path === '/register') && (
            <LogoutButton token={token} setTokenFunction={setTokenFunction} />
          )}
        </Toolbar>
      </AppBar>
    </Box>
  );
}

export default Header;
