// Imports
import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { GlobalStyle, StyledApp } from './appStyle.js';

import Dashboard from './pages/Dashboard';
import Login from './pages/Login';
import Register from './pages/Register';
import Slides from './pages/Slides';
import Slide from './pages/Slide';

// Main App function
function App () {
  // Get user token from the local storage
  let lsToken = '';
  if (localStorage.getItem('userToken')) {
    lsToken = localStorage.getItem('userToken');
  }
  // Store user token in the local storage
  const setTokenAbstract = (token) => {
    setToken(token);
    localStorage.setItem('userToken', token);
  }

  const [token, setToken] = React.useState(lsToken);
  React.useEffect(() => {
    setTokenAbstract(token);
    console.log('User token: ', token);
  }, [token])

  const [cards, setCards] = React.useState([]);
  React.useEffect(() => {
    console.log('Current cards array:', cards);
  }, [cards]);

  return (
    <>
      <BrowserRouter>
        <GlobalStyle />
        <StyledApp>
            <Routes>
              <Route path="/" element={<Dashboard token={token} setTokenFunction={setTokenAbstract} cards={cards} setCardsFunction={setCards} />} />
              <Route path="/register" element={<Register token={token} setTokenFunction={setTokenAbstract} />} />
              <Route path="/login" element={<Login token={token} setTokenFunction={setTokenAbstract} />} />
              {/* <Route path="/dashboard" element={<Dashboard token={token} setTokenFunction={setTokenAbstract} />} /> */}

              <Route path="/:id" element={<Slides token={token} setTokenFunction={setTokenAbstract} cards={cards} setCardsFunction={setCards} />}>
                <Route path="*" element={<Slide cards={cards} />} />
              </Route>
            </Routes>
        </StyledApp>
      </BrowserRouter>
    </>
  );
}

export default App;
