// import React, { useState } from 'react';
import React from 'react';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import TextField from '@mui/material/TextField';
const ModalStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  pt: 2,
  px: 4,
  pb: 3,
};
function CreateModal ({ cards, setCards }) {
  const [open, setOpen] = React.useState(false);
  const [projectNames, setProjectNames] = React.useState([]); // Store all project names 存储所有项目名称
  const [currentName, setCurrentName] = React.useState(''); // Currently entered project name 当前输入的项目名称
  const modalOpen = () => {
    setOpen(true);
  };
  const modalClose = () => {
    setOpen(false);
  };
  // To make sure the name has uniqueness and not empty
  const cardCreate = async () => {
    if (projectNames.includes(currentName)) {
      alert('Project name exists');
      return; // Stop the function if the project name exists
    }
    if (currentName.trim() === '') {
      alert('Project name can\'t be empty');
      return; // Stop the function if the project name is empty
    }
    const encodedName = btoa(currentName); // Base64 encode the project name
    const newID = `${encodedName}`; // Create the URL with the encoded name
    console.log('New page created with ID:', newID);
    modalClose(); // Close modal box 关闭模态框
    setProjectNames([...projectNames, currentName]);
    setCurrentName(''); // Clear the current input 清空当前输入
    const newCard = {
      projectName: currentName,
      image: '',
      url: `/${newID}`, // Adding a new URL to the card's data 将新URL添加到卡片的数据中
      projectContent: '', // Here could be a description of the content 这里可以是内容的描述
      projectPage: '1',
      slides: []
    };
    console.log('Creating new card with URL:', newCard.url);
    setCards([...cards, newCard]); // add new card in cards array 添加新卡片到数组中
  }
  const handleNameChange = (event) => {
    setCurrentName(event.target.value);
  };
  return (
    <>
      <Button variant="contained" onClick={modalOpen}>New presentation</Button>
      <Modal
        open={open}
        onClose={modalClose}
        aria-labelledby="child-modal-title"
        aria-describedby="child-modal-description"
      >
        <Box sx={{ ...ModalStyle, width: 300 }}>
          <h2 id="child-modal-title">Create a new project</h2>
          <TextField id="outlined-basic" label="Project Name" variant="outlined" onChange={handleNameChange} sx={{ mb: 3, width: '100%', '& .MuiInputLabel-root': { fontSize: '16px' }, }} fullWidth/>
          <Button variant="contained" size="small" onClick={cardCreate} type="submit">Create</Button>
        </Box>
      </Modal>
    </>
  );
}
export default CreateModal;
